import { useState, useEffect } from 'react';
import { useStore } from './useStore';
import andarSom from '../audio/andar.wav';



function actionByKey(key) {
  const keys = {
    KeyW: 'moveForward',
    KeyS: 'moveBackward',
    KeyA: 'moveLeft',
    KeyD: 'moveRight',
    Space: 'jump',
  };
  return keys[key];
}




function textureByKey(key) {
  const keys = {
    Digit1: 'dirt',
    Digit2: 'grass',
    Digit3: 'glass',
    Digit4: 'wood',
    Digit5: 'log',
    Digit6: 'dirtgrass',
  };
  return keys[key];
}
export const useKeyboardControls = () => {

  const [movement, setMovement] = useState({
    moveForward: false,
    moveBackward: false,
    moveLeft: false,
    moveRight: false,
    jump: false,
  });

  const [setTexture] = useStore((state) => [state.setTexture]);
  //const [plaingCount,setPlaingCount] = useState(0);
  const [play] = useState(new Audio(andarSom));
  let plaingCount = 0;

  useEffect(() => {

    const teclaKeyDown = (e) => {
    console.log("down");

      console.log(plaingCount);
      if(plaingCount===0)
      {
        if(play.ended)
        {
          play.pause();
        }
        play.play();
        plaingCount = 1;
      }
    }
    const teclaKeyUp = (e) => {
      if(plaingCount==1){
      plaingCount--;
      console.log(plaingCount);
      play.pause();
    }
    }
    document.addEventListener('keydown', teclaKeyDown);
    document.addEventListener('keyup', teclaKeyUp);

    return () => {
      document.removeEventListener('keydown', teclaKeyDown);
      document.removeEventListener('keyup', teclaKeyUp);
    };
  }, []);


  useEffect(() => {

    const handleKeyDown = (e) => {

      // Movement key
      if (actionByKey(e.code)) {
        setMovement((state) => ({
          ...state,
          [actionByKey(e.code)]: true,
        }));
      }
      // Change texture key
      if (textureByKey(e.code)) {
        setTexture(textureByKey(e.code));
      }
    };
    const handleKeyUp = (e) => {

      if (actionByKey(e.code)) {
        setMovement((state) => ({
          ...state,
          [actionByKey(e.code)]: false,
        }));
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keyup', handleKeyUp);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keyup', handleKeyUp);
    };
  }, [setTexture]);

  return movement;
};
