import React, { useState, useEffect, setIsLoaded, setError, setItems} from 'react';


export const Controls = (props) => {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    useEffect(() => {
        fetch("https://api.example.com/items")
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setItems(result);
            },
            // Nota: é importante lidar com errros aqui
            // em vez de um bloco catch() para não receber
            // exceções de erros reais nos componentes.
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
          )
      }, [])

    const [mostraSalvar, setDivSalvar] = useState(false);

    const abreDivSalvar = () => {
        setDivSalvar(true);
        }

    const fechaDivSalvar = () => {
        setDivSalvar(false);
    }


return(
    <>
    <div className="botoesCarregar btn" >Abrir Mapa</div>
    <div className="botoesSalvar btn" onClick={abreDivSalvar} >Salvar Mapa</div>
    <div className="botoesZerar btn" ><a href="/" className="linkOff">Zerar Mapa</a></div>
    <div className="quadroMapas"></div>
    <div className="quadroSalvar" style={{ display: mostraSalvar ? "block" : "none" }}>
    <div className="fecharBtn" onClick={fechaDivSalvar}>X</div>
        <form >
            <h3>Compartilhe seu mapa no servidor</h3>
            <hr></hr>
            <div className="divForm">
                <label>Nome:</label>
                <input placeholder="Digite seu nome" className="inputForm"/>
            </div>
            <div className="espaco"></div>
            <div className="divForm">
                <label>Titulo:</label>
                <input placeholder="Digite um título para o seu mapa" className="inputForm"/>
            </div>
            <div className="espaco"></div>
            <div className="divForm">
                <div className="flexCenter">
                    <input className="buttonForm" type="button" value="SALVAR" />
                </div>
            </div>
            <input type="hidden" />
        </form>
    </div>
    </>
)};
