    <div className="botoesZerar btn" ><a href="/" className="linkOff">Zerar Mapa</a></div>
    <div className="quadroMapas quadro"  style={{ display: mostraMapas ? "block" : "none" }}>
        <h3>Lista de mapas salvos no servidor</h3>
