import React, {useRef} from "react"
import {useThree, useFrame} from "react-three-fiber"



export const Arm = (position) => {
    const { camera } = useThree()
  const ref = useRef()

  useFrame(() => (
      ref.current.position.x = camera.position.x,
      ref.current.position.y = camera.position.y,
      ref.current.position.z = camera.position.z+0.3
      //camera.position.copy(ref.current.position);
      ))

  return (
    <mesh position={[1,1,1]} ref={ref}>
      <boxBufferGeometry args={[0.2, 0.4, 0.2]} attach="geometry" />
      <meshPhongMaterial color="black"  attach="material" />
    </mesh>
  )
}
