import React from 'react';
import { Canvas } from 'react-three-fiber';
import { Sky } from 'drei';
import { Physics } from 'use-cannon';

import { Ground } from './components/Ground';
import Cubes from './components/Cubes';
import { Player } from './components/Player';
import { Hud } from './components/Hud';
import { Controls } from './components/Controls';

function App() {

  return (
    <>
    <Controls /> 
    <Canvas className="canvas" shadowMap sRGB camera={{ position: [0, 0, 2.5], fov: 69, far: 30 }} >
      <Sky sunPosition={[10, 2, 10]} />
      <ambientLight intensity={0.25}  />
      <fog attach="fog" args={['#ccc', 1, 35]} />
      <pointLight castShadow intensity={0.7} position={[10, 10, 10]}   />
      <Hud position={[0, 0, -2]} />
      <Physics gravity={[0, -30, 0]} tolerance={0.0005}>
        <Ground position={[0, 0.05, 0]} />
        <Player position={[0, 0.3, 0]} />
        <Cubes />
      </Physics>
    </Canvas>
    </>
  );
}

export default App;
