import React, { useState, useEffect} from 'react';
import useForm from "../hooks/useForm";

export const Controls = (props) => {


    const [items, setItems] = useState([]);

    const [{ values, loading }, handleChange, handleSubmit] = useForm();

    const enviarContato = () => {
        var url = 'https://cors-anywhere.herokuapp.com/http://rodrigo-kulb.com.br/teste.php?nome='+values.nome +'&titulo='+values.titulo;
        fetch(url, {
          method: 'POST',
          headers:{
            'X-Requested-With': 'XMLHttpRequest'
          }
        }).then(res => res.json())
        .then(response => console.log('Success:', response))
        .catch(error => console.error('Error:', error));

      console.log(values);
    };


    const [mostraSalvar, setDivSalvar] = useState(false);

    const abreDivSalvar = () => {
        setDivSalvar(true);
        }

    const fechaDivSalvar = () => {
        setDivSalvar(false);
    }


return(
    <>
    <div className="botoesCarregar btn" >Abrir Mapa</div>
    <div className="botoesSalvar btn" onClick={abreDivSalvar} >Salvar Mapa</div>
    <div className="botoesZerar btn" ><a href="/" className="linkOff">Zerar Mapa</a></div>
    <div className="quadroMapas"></div>
    <div className="quadroSalvar" style={{ display: mostraSalvar ? "block" : "none" }}>
    <div className="fecharBtn" onClick={fechaDivSalvar}>X</div>
    <ul>
        {items.map(item => (
          <li key={item.id}>
            {item.name} {item.price}
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit(enviarContato)}>
            <h3>Compartilhe seu mapa no servidor</h3>
            <hr></hr>
            <div className="divForm">
                <label>Nome:</label>
                <input placeholder="Digite seu nome" name="nome" className="inputForm" onChange={handleChange}/>
            </div>
            <div className="espaco"></div>
            <div className="divForm">
                <label>Titulo:</label>
                <input  placeholder="Digite um tÃ­tulo para o seu mapa" name="titulo"  className="inputForm" onChange={handleChange}/>
            </div>
            <div className="espaco"></div>
            <div className="divForm">
                <div className="flexCenter">
                    <button className="buttonForm" type="submit">{loading ? "Salvando..." : "SALVAR"}</button>
                </div>
            </div>
            <input type="hidden" />
        </form>
    </div>
    </>
)};
